import {FETCH_DRIVERS, REMOVE_DRIVER} from "./types";
import { DriversActionTypes } from "./types";
import {IDriver} from "../../shared/interfaces/IDriver";



export const driversReducer = (state:Array<IDriver> = [], action: DriversActionTypes): any => {
    switch (action.type) {
        case FETCH_DRIVERS:
            return action.payload;
        case REMOVE_DRIVER:
            debugger;
            return [
                ...state,
                state.filter(driver => {
                    driver._id !== action.payload
                }),
            ];
        default:
            return state;
    }
};
