import {ITask} from "../../shared/interfaces/ITask";
import {IDriver} from "../../shared/interfaces/IDriver";

export const FETCH_TASKS = 'FETCH_TASKS';
export const ASSIGN_DRIVER = 'ASSIGN_DRIVER';

export interface TasksState {
    tasks: Array<ITask>;
}

export interface TasksFetchAction {
    type: typeof FETCH_TASKS;
    payload: Array<ITask>
}

export interface AssignDriverAction {
    type: typeof  ASSIGN_DRIVER;
    payload: {
        driver: IDriver,
        taskId: string
    };
}

export type TasksActionTypes = TasksFetchAction;
