import {Dispatch} from "redux";
import Axios from "axios";
import {AssignDriverAction, FETCH_TASKS, TasksFetchAction, ASSIGN_DRIVER} from "./types";
import {TASKS_API} from "../../shared/contants/apis";
import {IDriver} from "../../shared/interfaces/IDriver";

export function fetchTasks(): (dispatch: Dispatch<TasksFetchAction>) => Promise<void> {
    return async (dispatch: Dispatch<TasksFetchAction>) => {
        const { data } = await Axios.get(TASKS_API);
        dispatch({
            type: FETCH_TASKS,
            payload: data
        })
    }
}

export function assignDriverAction(driver: IDriver, taskId: string): AssignDriverAction {
    return {
        type: ASSIGN_DRIVER,
        payload: {
            driver,
            taskId
        },
    }
}
