import {DriversState, FETCH_DRIVERS, FILTER_DRIVERS, REMOVE_DRIVER} from "./types";
import { DriversActionTypes } from "./types";
import {IDriver} from "../../shared/interfaces/IDriver";

const initialDriversState: DriversState = {
    drivers: [],
    filterBy: ''
};

export const driversReducer = (state: DriversState = initialDriversState, action: DriversActionTypes): any => {
    switch (action.type) {
        case FETCH_DRIVERS:
            return {
                ...state,
                drivers: action.payload,
                filterBy: ''
            };
        case REMOVE_DRIVER:
            return state.filter(driver => driver._id !== action.payload);
        case FILTER_DRIVERS:
            return
        default:
            return state;
    }
};
