import {ASSIGN_DRIVER, FETCH_TASKS, TasksState} from "./types";
import { TasksActionTypes } from "./types";

const initialState: TasksState = {
    tasks: []
};

export const tasksReducer = (state = initialState.tasks, action: TasksActionTypes): any => {
    switch (action.type) {
        case FETCH_TASKS:
            return action.payload;
        case ASSIGN_DRIVER:
                state.map(task => {
                    if (task._id === action.payload.taskId) {
                        task.assignTo = action.payload.driver;
                    }
                });

            return [...state];

        default:
            return state;
    }
};
