import {FETCH_DRIVERS, DriversState, REMOVE_DRIVER} from "./types";
import { DriversActionTypes } from "./types";

const initialState: DriversState = {
    drivers: []
};

export const driversReducer = (state = initialState, action: DriversActionTypes): any => {
    switch (action.type) {
        case FETCH_DRIVERS:
            return action.payload;
        case REMOVE_DRIVER:
            return [...state.drivers, state.drivers, state.drivers.filter(driver => action.payload !== action.payload)]
        default:
            return state;
    }
};
