}

class DriverSelector extends React.Component<DriversSelectorProps, {taskId: string}> {
    componentDidMount(): void {
        console.log(this.props)
    }

    renderDriversList() {
        return <h1>test</h1>;
    }

    render() {
        return (
            <div className="driver-selector">
                <div className="driver-selector_wrapper">
                    <span className="driver-selector_wrapper__current">Test</span>
                </div>
                <div className="driver-selector_list">
                    {this.renderDriversList()}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state: Array<IDriver>) => {

    return state
};

