import React from 'react';
import {connect} from "react-redux";
import {IDriver} from "../../../../shared/interfaces/IDriver";
import {ITask} from "../../../../shared/interfaces/ITask";
import './driverSelect.scss'

interface OwnProps {
    task: ITask;
}

interface StateProps {
    drivers: Array<IDriver>;
}

interface DispatchProps {

}

type Props = OwnProps & StateProps & DispatchProps;

class DriverSelect extends React.Component<Props, {openModal: boolean}> {
    componentDidMount(): void {
        this.setState({
            openModal: false
        })
    }


    showDriversModal = () => {
        if(this.state && this.state.openModal) {
            return (
                <div className="driver-select_btn_modal">
                    <div className="driver-select_btn_modal_wrapper">
                        <div className="driver-select_btn_modal_wrapper">
                            {this.props.drivers.map(driver => {
                                return (
                                    <div onClick={this.onSelectedDriver} className="driver-select_btn_modal_wrapper_driver">
                                        <span className="driver-select_btn_modal_wrapper_driver__name">
                                            {driver.name}
                                        </span>
                                    </div>
                                )
                            })}
                        </div>
                    </div>
                </div>
            )
        }
    };

    onHandleSelectDriver = () => {
        this.setState({
            openModal: true
        });
    };

    render() {
        return (
            <div className="driver-select">
                <div onClick={this.onHandleSelectDriver} className="driver-select_btn">
                    <span className="driver-select_btn__selected">
                        {this.props.task.assignTo ? this.props.task.assignTo : 'Select Driver'}
                    </span>
                    { this.showDriversModal() }
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state: StateProps): StateProps => {
    return state;
};

export default connect(mapStateToProps, {})(DriverSelect);
