import {ILocation} from "../interfaces/ILocation";

export const parseLocation = (location: ILocation) => {
     return [parseFloat(location.latitude), parseFloat(location.longitude)];
};

export const sortBy = (array: Array<any>, by: string, ascending: boolean = true): Array<any> => {
     if(ascending) {
          array.sort(function(a, b){
               if(a[by] < b[by]) { return -1; }
               if(a[by]> b[by]) { return 1; }
               return 0;
          });
     } else {
          array.sort(function(a, b){
               if(a[by] < b[by]) { return 1; }
               if(a[by]> b[by]) { return -1; }
               return 0;
          });
     }

     return array
};
