import {ASSIGN_DRIVER, FETCH_TASKS, TasksState} from "./types";
import { TasksActionTypes } from "./types";

const initialState: TasksState = {
    tasks: []
};

export const tasksReducer = (state = initialState, action: TasksActionTypes): any => {
    switch (action.type) {
        case FETCH_TASKS:
            return action.payload;
        case ASSIGN_DRIVER:
            state.tasks.forEach(task => {
                console.log(task);
            });
            return state;

        default:
            return state;
    }
};
