// @ts-ignore
import { ReactBingmaps } from 'react-bingmaps';

import React from 'react';
import './map.scss';
import {IDriver} from "../../shared/interfaces/IDriver";
import {ITask} from "../../shared/interfaces/ITask";
import {connect} from 'react-redux';
import {MAP_API} from "../../shared/contants/apis";
import {parseLocation} from "../../shared/utils/utils";

export const DRIVER_ICON ="https://i.ibb.co/G0RsGJ7/driver-Icon.png";


interface StateProps {
    drivers: Array<IDriver>;
    tasks: Array<ITask>;
}

class Map extends React.Component<StateProps> {
    componentDidMount(): void {
        this.setState({
            currentLocation: [0,0]
        })
    }

    mapMarkers = () => {
        if(this.props.drivers.length) {
            const locations: Array<any> = [];
            this.props.drivers.forEach(driver => {
                const parsedLocation = parseLocation(driver.location);
                locations.push(
                    {
                        parsedLocation,
                        options: {
                            title: driver.name,
                            icon: DRIVER_ICON
                        }
                    }
                )
            });
            return locations;
        }
    };

    render() {
        // @ts-ignore
        return (
            <div className="map-container">
                <ReactBingmaps
                    bingmapKey={MAP_API}
                    center={[0,0]}
                    pushPins={this.mapMarkers()}
                />
            </div>
        )
    }
}

const mapStateToProps = (state: StateProps): StateProps => {
    console.log(state);
    return state;
};

export default connect(mapStateToProps, {})(Map);
