import {ASSIGN_DRIVER, FETCH_TASKS} from "./types";
import { TasksActionTypes } from "./types";
import {ITask} from "../../shared/interfaces/ITask";


export const tasksReducer = (state: Array<ITask> = [], action: TasksActionTypes): Array<ITask> => {
    switch (action.type) {
        case FETCH_TASKS:
            return action.payload;
        case ASSIGN_DRIVER:
                state.forEach((task, index) => {
                    if (task._id === action.payload.taskId) {
                        state[index].assignTo = action.payload.driver;
                    }
                });
            return [...state];

        default:
            return state;
    }
};
