import {DRIVERS_API} from "../../shared/contants/apis";
import {ThunkAction} from "redux-thunk";
import {Action} from "redux";
import {FETCH_DRIVERS} from "./types";
import Axios, {AxiosResponse} from "axios";
import {IDriver} from "../../shared/interfaces/IDriver";

export const fetchDrivers = (): ThunkAction<void, null, null, Action<string>> => async dispatch => {
    const response: AxiosResponse<Array<IDriver>> = await Axios.get(DRIVERS_API);

    dispatch({
        type: FETCH_DRIVERS,
        payload: response
    })

};
