import {FETCH_DRIVERS} from "./types";
import {DRIVERS_API} from "../../shared/contants/apis";
import {IDriver} from "../../shared/interfaces/IDriver";
import {ThunkAction, ThunkDispatch} from "redux-thunk";
import {AnyAction} from "redux";

interface DriversFetchAction {
    type: typeof FETCH_DRIVERS;
    payload: Array<IDriver>;
}

interface DriversResponse extends Response{
    drivers: Array<IDriver>
}

export const fetchDrivers = ():ThunkAction<Promise<D>, {}, {}, AnyAction>  => {

return async (dispatch: ThunkDispatch<{}, {}, AnyAction>): Promise<void>  => {
    const response: DriversResponse = await fetch(DRIVERS_API);

      dispatch({type: FETCH_DRIVERS, payload: response.drivers})
    }
};
