import {SET_LOCATION, MapState} from "./types";
import { MapActionTypes } from "./types";

const initialState: MapState = {
    currentLocation: [0,0]
};

export const mapReducer = (state = initialState, action: MapActionTypes): any => {
    debugger;
    switch (action.type) {
        case SET_LOCATION:
            return [...action.payload];
        default:
            return state;
    }
};
