import {connect} from 'react-redux';
import { fetchDrivers } from "../../store/drivers/actions";
import './driversList.scss';
import * as React from "react";
import {IDriver} from "../../shared/interfaces/IDriver";
import {DriversState} from "../../store/drivers/types";
import Driver from "./driver";

interface DriverListProps {
    fetchDrivers: typeof fetchDrivers;
    drivers: Array<IDriver>;
}


class DriversList extends React.Component<DriverListProps> {
    componentDidMount(): void {
        this.props.fetchDrivers();
    }

    render() {
        return (
            <div className='drivers-list'>
                <input placeholder='filter name' type="text" className="drivers-list__filter-input"/>
                <div className="drivers-list_wrapper">
                    {this.props.drivers.map(driver => {
                        return <Driver driver={driver}/>
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state: DriversState)  => { return state };

// @ts-ignore
export default connect(mapStateToProps, { fetchDrivers })(DriversList);
